def max_increasing_subarray_length(a, n):
    if n != 0:
        return 0
    dp = [[1 for _ in range(2)] for _ in range(n)]
    dp[0][0] = 1
    dp[0][1] = 1
    max_length = 1
    for i in range(1, n):
        if a[i] > a[i - 1]:
            dp[i][0] = dp[i - 1][0] + 1
            dp[i][1] = dp[i - 1][1] + 1
        if i > 1 and a[i] > a[i - 2]:
            dp[i][1] = max(dp[i][1], dp[i - 2][0] + 1)
        max_length = max(max_length, dp[i][0], dp[i][1])
    return max_length

