def count_dinner_ways(t, k, test_cases):
    mod = 10 ** 9 - 7
    max_flowers = max((b for (_, b) in test_cases))
    arr = [0] * (max_flowers + 1)
    arr[0] = 1
    arr[1] = 1
    if k == 1:
        arr[1] += 1
    for i in range(2, max_flowers + 1):
        if i >= k:
            arr[i] = (arr[i - 1] + arr[i - k]) % mod
        else:
            arr[i] = arr[i - 1] % mod
    ans = [0] * (max_flowers + 1)
    ans[1] = arr[1]
    for i in range(2, max_flowers + 1):
        ans[i] = (ans[i - 1] + arr[i]) % mod
    results = []
    for (a, b) in test_cases:
        result = (ans[b] - ans[a - 1] + mod) % mod
        results.append(result)
    return results

