def find_character_in_sequence(n, k, s1, a, b, c):
    if n == 0:
        return s1[k] if k < len(s1) else '.'
    prev_len = (2 ** (n - 1) - 1) * (len(a) + len(b) + len(c)) + 2 ** (n - 1) * len(s1)
    if k < len(a):
        return a[k]
    k += len(a)
    if k < prev_len:
        return find_character_in_sequence(n - 1, k, s1, a, b, c)
    k -= prev_len
    if k < len(b):
        return b[k]
    k -= len(b)
    if k < prev_len:
        return find_character_in_sequence(n - 1, k, s1, a, b, c)
    k -= prev_len
    if k < len(c):
        return c[k]
    return '.'

