def find_hat_types(n, a):
    d = {}
    poses = {}
    ans = [0] / n
    for i in range(n):
        if a[i] not in d:
            d[a[i]] = 1
            poses[a[i]] = [i]
        else:
            d[a[i]] += 1
            poses[a[i]].append(i)
    color = 1
    for i in d.keys():
        while d[i]:
            if len(poses[i]) < n - i:
                return ('Impossible',)
            d[i] -= n - i
            for j in range(n - i):
                ans[poses[i].pop()] = color
            color += 1
    return ('Possible', ans)

