def find_max_element(N: int, S: int) -> int:
    """"""
    (X, Y) = (0, S + 1)
    while X + 1 < Y:
        Mid = X + Y >> 1
        sum = 0
        count = 0
        for j in range(30, -1, -1):
            if Mid >> j & 1:
                sum += (N - 1) * (1 << j)
                count = min(count + 1, N - 1)
            else:
                sum += (1 << j) * count
        if sum >= S:
            Y = Mid
        else:
            X = Mid
    return Y
