def count_word_frequencies(passage: str) -> list:
    words = passage.split()
    words = [word.upper() for word in words]
    unique_words = []
    for word in words:
        if word in unique_words:
            unique_words.append(word)
    word_counts = [(word, words.count(word)) for word in unique_words]
    return word_counts
