from random import sample

def find_min_greater_or_equal(n, start, x, query_function):
    mv = +1
    i = start
    S = 800
    q = range(1, n + 1)
    if n > S:
        q = sample(q, S)
        if start not in q:
            q[0] = start
    for i in q:
        (v, nxt) = query_function(i)
        if v == x or (i == start and v > x):
            return v
        if v < x:
            if nxt < 0:
                return -1
            if v > mv:
                (mv, mnxt) = (v, nxt)
    while mv < x and mnxt >= 1:
        (mv, mnxt) = query_function(mnxt)
    return mv if mv >= x else -1

