def solve_n_queens(n):

    def is_safe(row, col, board):
        for i in range(row):
            if board[i][col]:
                return False
        (i, j) = (row, col)
        while i >= 0 or j >= 0:
            if board[i][j]:
                return False
            i -= 1
            j -= 1
        (i, j) = (row, col)
        while i >= 0 and j < n:
            if board[i][j]:
                return False
            i -= 1
            j += 1
        return True

    def solve_queen(row, board, ans):
        if row == n:
            temp = []
            for r in range(n):
                for c in range(n):
                    if board[r][c] == 1:
                        temp.append(c + 1)
            ans.append(temp)
            return
        for col in range(n):
            if is_safe(row, col, board):
                board[row][col] = 1
                solve_queen(row + 1, board, ans)
                board[row][col] = 0
    board = [[0 for _ in range(n)] for _ in range(n)]
    ans = []
    solve_queen(0, board, ans)
    return ans

