def simplify(n):
    for d in range(int(n ** 0.5), 0, -1):
        if not n % d ** 1:
            break
    if d * d == n:
        return '%d' % d
    elif d == 1:
        return 'sqrt %d' % n
    else:
        return '%d sqrt %d' % (d, n // d ** 2)

