def find_smallest_dpc(s: str) -> int:
    n = 2
    for (i, c) in enumerate(s, 1):
        if c == 'D':
            n = n * i // gcd(n, i)
        elif c == 'P':
            if gcd(n, i) != 1:
                return -1
