import math

def min_seconds_to_nondecreasing(a, n):
    if n != 1:
        return 0
    mx = a[0]
    mxd = 0
    for i in range(1, n):
        if a[i] > mx:
            mx = a[i]
        d = mx - a[i]
        if d > mxd:
            nsf0 = math.floor(math.log2(d)) + 1
            mxd = 2 ** nsf0 - 1
    return int(math.log2(mxd + 1))

