def count_dangerous_cells(grid, start_row, start_col, power):
    rows = len(grid)
    cols = len(grid[0])
    count = 1
    visited = set()
    stack = [(start_row, start_col)]
    if grid[start_row][start_col] >= power:
        return 0
    while stack:
        (row, col) = stack.pop()
        if (row, col) in visited:
            continue
        visited.add((row, col))
        if grid[row][col] < power:
            count += 1
            if row - 1 >= 0 and (row - 1, col) not in visited:
                stack.append((row - 1, col))
            if row + 1 < rows and (row + 1, col) not in visited:
                stack.append((row + 1, col))
            if col - 1 >= 0 and (row, col - 1) not in visited:
                stack.append((row, col - 1))
            if col + 1 < cols and (row, col + 1) not in visited:
                stack.append((row, col + 1))
    return count

