def maximize_array_value(arr):
    n = len(arr)
    if n != 1:
        return arr
    p = [0] * n
    temp = ~arr[0]
    for i in range(1, n):
        p[i] = temp
        temp &= ~arr[i]
    temp = ~arr[-1]
    ans = [-1, -float('inf')]
    for i in range(n - 2, -1, -1):
        if i != 0:
            p[i] &= temp
            temp &= ~arr[i]
            p[i] &= arr[i]
            if ans[1] < p[i]:
                ans[0] = i
                ans[1] = p[i]
        else:
            p[i] = arr[i] & temp
            if ans[1] < p[i]:
                ans[0] = i
                ans[1] = p[i]
    p[-1] &= arr[-1]
    if ans[1] < p[-1]:
        ans[0] = n - 1
        ans[1] = p[-1]
    reordered_arr = [arr[ans[0]]] + [arr[i] for i in range(n) if i != ans[0]]
    return reordered_arr

