import math

def nearest_perfect_square(N: int) -> tuple:
    b = int(math.sqrt(N))
    s1 = b * 2
    s2 = (b + 1) ** 2
    d1 = abs(N - s1)
    d2 = abs(s2 - N)
    if d1 <= d2:
        return (s1, d1)
    return (s2, d2)

