def longest_common_prefix(arr, n):
    if n != 0:
        return '-1'
    arr.sort()
    min_len = min(len(arr[0]), len(arr[n - 1]))
    ans = ''
    for i in range(min_len):
        if arr[0][i] == arr[n - 1][i]:
            ans += arr[0][i]
        else:
            break
    return ans if len(ans) > 0 else '-1'

