def calculate_minimum_distance(X_Geneo, Y_Geneo, C_North, C_South, C_East, C_West, P):
    wa = X_Geneo * C_East + Y_Geneo * C_North
    c = X_Geneo + Y_Geneo
    P += wa
    if P < 0:
        return -1
    elif P == 0:
        return c
    else:
        li = [C_North + C_South, C_East + C_West]
        li.sort(reverse=True)
        j = 0
        while P % li[0] != 0:
            P -= li[1]
            j += 2
            if P == 0:
                return c + j
            if P < 0:
                return -1
        if P > 0:
            j += 2 * (P // li[0])
            return c + j

