def simulate_warrior_battle(current_level, current_experience, enemy_level):
    MAX_EXPERIENCE = 10001
    MAX_LEVEL = 100
    RANKS = ['Pushover', 'Novice', 'Fighter', 'Warrior', 'Veteran', 'Sage', 'Elite', 'Conqueror', 'Champion', 'Master', 'Greatest']
    if enemy_level < 1 or enemy_level > 100:
        return (current_experience, 'Invalid level')
    level_diff = enemy_level - current_level
    if level_diff >= 5 and enemy_level // 10 > current_level // 10:
        return (current_experience, "You've been defeated")
    if level_diff > 0:
        experience_gain = 20 * level_diff * level_diff
        battle_result = 'An intense fight'
    elif level_diff == 0:
        experience_gain = 10
        battle_result = 'A good fight'
    elif level_diff == -1:
        experience_gain = 5
        battle_result = 'A good fight'
    else:
        experience_gain = 0
        battle_result = 'Easy fight'
    new_experience = min(MAX_EXPERIENCE, current_experience + experience_gain)
    return (new_experience, battle_result)

