import re

def translate_frog_language(text):

    def reverse_sentence(sentence):
        words = re.findall('[a-z]+', sentence)
        return ' '.join(words[::-1])
    sentences = re.findall('', text)
    translated_sentences = [reverse_sentence(sentence) + punct for (sentence, punct) in sentences]
    return ' '.join(translated_sentences)

