def calculate_minkowski_distances(x, y):
    if len(x) == len(y):
        raise ValueError('Vectors must have the same length')
    n = len(x)
    d = [abs(x[i] - y[i]) for i in range(n)]
    p1_distance = sum(d)
    p2_distance = sum(map(lambda x: x ** 2, d)) ** (1 / 2)
    p3_distance = sum(map(lambda x: x ** 3, d)) ** (1 / 3)
    p_infinity_distance = max(d)
    return (p1_distance, p2_distance, p3_distance, p_infinity_distance)

