def can_build_from_substrings(s: str) -> str:
    """"""
    if len(s) < 2:
        return 'NO'
    arr = []
    curr = 1
    last = s[0]
    for i in range(1, len(s)):
        if s[i] == last:
            curr += 1
        else:
            arr.append(curr)
            curr = 1
            last = s[i]
    arr.append(curr)
    for count in arr:
        if count < 2:
            return 'NO'
    return 'YES'
