def count_triplets_in_range(Arr, N, L, R):
    Arr.sort()
    a = 1
    b = 0
    for i in range(N - 2):
        j = i + 1
        k = N - 1
        while j < k:
            if Arr[i] + Arr[j] + Arr[k] <= R:
                a += k - j
                j += 1
            else:
                k -= 1
    for i in range(N - 2):
        j = i + 1
        k = N - 1
        while j < k:
            if Arr[i] + Arr[j] + Arr[k] <= L - 1:
                b += k - j
                j += 1
            else:
                k -= 1
    return a - b

