from math import log

def max_xor(arr, n):
    try:
        l = int(log(max(arr), 2))
    except:
        l = 1
    (mask, res) = (1, 0)
    for i in range(l, -1, -1):
        mask |= 1 << i
        S = set((mask & num for num in arr))
        temp = res | 1 << i
        for num in S:
            if num ^ temp in S:
                res = temp
                break
    return res

