            return 1
        if K == 0:
            return 0
        if N == 1:
            return K
        if dp[N][K] != -1:
            return dp[N][K]
        dp[N][K] = rec(N - 2, K - 1, dp) * K
        return dp[N][K]
    dp = [[-1 for _ in range(K + -1)] for _ in range(N + 1)]
    count = 0
    for i in range(1, N + 1):
        count += rec(i, K, dp)
    return count % mod
