def calculate_max_increasing_trend_and_prime_money(A, N):

    def is_prime(num):
        if num <= 1:
            return False
        if num <= 3:
            return True
        if num % 2 == 0 or num % 3 == 0:
            return False
        i = 5
        while i * i <= num:
            if num % i == 0 or num % (i + 2) == 0:
                return False
            i += 6
        return True
    maxi = max(A)
    primes = set()
    for i in range(2, maxi + 1):
        if is_prime(i):
            primes.add(i)
    count = 1
    summa = 0
    last = 0
    temp_count = 0
    temp_summa = 0
    for item in A:
        if item > last:
            temp_count += 1
            if item in primes:
                temp_summa += item
        else:
            if temp_count > count:
                count = temp_count
                summa = temp_summa
            elif temp_count == count:
                summa = max(summa, temp_summa)
            temp_count = 1
            if item in primes:
                temp_summa = item
            else:
                temp_summa = 0
        last = item
    if temp_count > count:
        count = temp_count
        summa = temp_summa
    elif temp_count == count:
        summa = max(summa, temp_summa)
    return (count, summa)

