from math import comb

def can_equally_distribute_wins(N):
    matche = comb(N, 2)
    win = matche * N
    if win != int(win):
        return 'NO'
    ans = []
    for k in range(N):
        list = [0] * N
        ans.append(list)
    for k in range(N):
        temp = int(win)
        index = 0
        while temp != 0:
            if index + 1 != k + 1 and ans[k][index] != 'set':
                ans[k][index] = 1
                ans[index][k] = 'set'
                temp -= 1
            index += 1
    for k in range(N):
        for j in range(N):
            if ans[k][j] == 'set':
                ans[k][j] = 0
    result = [''.join(map(str, row)) for row in ans]
    return ('YES', result)

