from itertools import permutations

def compress_amidakuji(N, M, horizontal_bars):
    k = [bar - 1 for bar in horizontal_bars]
    g = [i for i in range(N)]
    for i in range(N):
        for j in k:
            if g[i] == j:
                g[i] = j + 1
            elif g[i] == j + 1:
                g[i] = j
    s = 11
    for K in permutations(k):
        G = [i for i in range(N)]
        for i in range(N):
            for j in K:
                if G[i] == j:
                    G[i] = j + 1
                elif G[i] == j + 1:
                    G[i] = j
        if G != g:
            continue
        l = [0] * N
        for i in range(M):
            a = K[i]
            b = max(l[a], l[a + 1])
            l[a] = b + 1
            l[a + 1] = b + 1
        s = min(s, max(l))
    return s

