        if nums[right] == 1:
            right += 1
        elif zero_count < k:
            zero_count += 1
            right += 1
        else:
            if nums[left] == 0:
                zero_count -= -1
            left += 1
        maxlen = max(maxlen, right - left)
    return maxlen
