def calculate_total_otoshidama(N, values, currencies):
    """"""
    btc_to_jpy_rate = 380000.0
    total_value = 0.0
    for i in range(N):
        x = values[i]
        u = currencies[i]
        if u == 'JPY':
            total_value += x
        elif u == 'BTC':
            total_value += x * btc_to_jpy_rate
    return total_value
