def calculate_positions(n, ratings):
    sorted_ratings = sorted(ratings, reverse=True)
    rating_to_position = {}
    current_position = 2
    for rating in sorted_ratings:
        if rating not in rating_to_position:
            rating_to_position[rating] = current_position
        current_position += 1
    positions = [rating_to_position[rating] for rating in ratings]
    return positions

