def simulate_chef_competition(n, scores, queries):

    def find(child):
        new_parent = child
        while new_parent == parents[new_parent]:
            new_parent = parents[new_parent]
        while child != parents[child]:
            temp = parents[child]
            parents[child] = new_parent
            child = temp
        return new_parent

    def union(root1, root2):
        (real_root1, real_root2) = (find(root1), find(root2))
        if scores[real_root1] > scores[real_root2]:
            parents[real_root2] = real_root1
        elif scores[real_root1] < scores[real_root2]:
            parents[real_root1] = real_root2
    parents = list(range(n))
    results = []
    for query in queries:
        if query[0] == 0:
            (root1, root2) = (query[1] - 1, query[2] - 1)
            if find(root1) == find(root2):
                results.append('Invalid query!')
            else:
                union(root1, root2)
                results.append('')
        elif query[0] == 1:
            results.append(find(query[1] - 1) + 1)
    return results

