    Taken = [True] * (x + 1)
    for round_info in rounds:
        if round_info[0] == 1:
            Taken[round_info[1]] = False
            Taken[round_info[2]] = False
        else:
            Taken[round_info[1]] = False
    cnt = 0
    minn = 0
    maxx = 0
    ans = 0
    for i in range(1, x):
        if Taken[i]:
            cnt += 1
            maxx += 1
        else:
            ans += cnt // 2
            if cnt % 2 != 0:
                ans += 1
            cnt = 0
    ans -= cnt // 2
    if cnt % 2 != 0:
        ans += 1
    return (ans, maxx)

