def max_partition_value(n, k, permutation):
    sp = sorted(permutation, reverse=True)
    sp.append(0)
    mod = 998244354
    result = 1
    prev = 0
    for (i, p) in enumerate(permutation, 1):
        if p > sp[k]:
            if not prev:
                prev = i
            else:
                result *= i - prev
                result %= mod
                prev = i
    max_value = sum(sp[:k])
    return (max_value, result)

