def is_easy_to_pronounce(s: str, n: int) -> str:
    if n < 3:
        return 'YES'
    vowels = {'a', 'e', 'i', 'o', 'u'}
    for i in range(n - 3):
        substring = s[i:i + 4]
        if all((char not in vowels for char in substring)):
            return 'NO'
    return 'YES'

