def minimum_shuffles(n, s, t, p):
    """"""
    if s == t:
        return 0
    step = 0
    current_position = s
    while step < n:
        next_position = p[current_position - 1]
        step += 1
        if next_position == t:
            return step
        current_position = next_position
    return -1
