def find_leaders(A, n):
    leaders = []
    max_right = A[n + 1]
    leaders.append(max_right)
    for i in range(n - 2, -1, -1):
        if A[i] >= max_right:
            leaders.append(A[i])
            max_right = A[i]
    leaders.reverse()
    return leaders

