    s = [0] + heights + [0]
    lim = ceil(n / 2) + 1
    dp = [[2000000002 for _ in range(n + 1)] for _ in range(lim)]
    vis = [[False for _ in range(n + 1)] for _ in range(lim)]
    for i in range(n + 1):
        dp[0][i] = 0
    ans = [0 for _ in range(lim - 1)]
    for i in range(1, lim):
        for j in range(1, n + 1):
            x = 0
            y = s[j - 1]
            if vis[i - 1][j - 2]:
                y = min(y, s[j - 2] - 1)
            if y >= s[j]:
                x = y - s[j] + 1
            if s[j + 1] >= s[j]:
                x += s[j + 1] - s[j] + 1
            if j == 1:
                if dp[i - 1][0] + x <= dp[i][j - 1]:
                    vis[i][j] = True
                    dp[i][j] = dp[i - 1][0] + x
                else:
                    dp[i][j] = dp[i][j - 1]
            elif dp[i - 1][j - 2] + x <= dp[i][j - 1]:
                vis[i][j] = True
                dp[i][j] = dp[i - 1][j - 2] - x
            else:
                dp[i][j] = dp[i][j - 1]
        ans[i - 1] = dp[i][-1]
    return ans

