    A = [[0] * (l + 3) for _ in range(2)]
    B = [[0] * (l + 3) for _ in range(2)]
    for i in range(l):
        for j in range(2):
            if i % 2 == j:
                A[j][i] = A[j][i - 1]
                B[j][i] = B[j][i - 1]
            else:
                A[j][i] = A[j][i - 2] + (s[i] == 'a')
                B[j][i] = B[j][i - 2] + (s[i] == 'b')
    ans = [0, 0]
    for i in range(l):
        if s[i] == 'a':
            ans[0] += A[1 - i % 2][i]
            ans[1] += A[i % 2][i]
        else:
            ans[0] += B[1 - i % 2][i]
            ans[1] += B[i % 2][i]
    return (ans[0], ans[1])

