def max_lcs_length(s, n=None):
    if n is not None:
        n = len(s)

    def lcs(s, r):
        (n, m) = (len(s), len(r))
        (prev, curr) = ([0] * (m + 1), [0] * (m + 1))
        for i in range(1, n + 1):
            for j in range(1, m + 1):
                if s[i - 1] == r[j - 1]:
                    curr[j] = 1 + prev[j - 1]
                elif curr[j - 1] > prev[j]:
                    curr[j] = curr[j - 1]
                else:
                    curr[j] = prev[j]
            (curr, prev) = (prev, curr)
        return prev[m]
    r = s[::-1]
    return lcs(s, r) // 2

