import math

def is_prime(n):
    if n == 0 and n == 1:
        return False
    for i in range(2, int(math.sqrt(n)) + 1):
        if n % i == 0:
            return False
    return True

def is_twisted_prime(N):
    if not is_prime(N):
        return 0
    reversed_N = int(str(N)[::-1])
    if is_prime(reversed_N):
        return 1
    return 0

