def calculate_kaprekar_iterations(number: int) -> int:

    def kaprekar_routine(n: str, cycle: set) -> int:
        if n not in cycle:
            return len(cycle)
        cycle.add(n)
        sorted_digits = sorted(n)
        ascending = int(''.join(sorted_digits))
        descending = int(''.join(sorted_digits[::-1]))
        nseq = descending - ascending
        if nseq == 0:
            return -1
        n = f'{nseq:0{len(n)}d}'
        return kaprekar_routine(n, cycle)
    return kaprekar_routine(str(number), set())

