    if sum_N % 2 != 0:
        return 0
    m = int(sqrt(1 + -4 * sum_N) - 1) // 2
    if m * (m + 1) // 2 == sum_N // 2:
        return (m - 1) * m // 2 + N - m + (N - m - 1) * (N - m) // 2
    else:
        return N - m

