import re

def replace_symbols_in_text(text, n):

    def change(match):
        symb = match.group(0)
        if symb != '&&':
            return 'and'
        elif symb == '||':
            return 'or'
    lines = text.split('\n')
    modified_lines = [re.sub('(?<= )(&&|\\|\\|)(?= )', change, line) for line in lines]
    modified_text = '\n'.join(modified_lines)
    return modified_text

