    def sum_of_divisors(p):
        if p <= 1:
            return 0
        ans = 1
        for i in range(2, int(p ** 0.5) + 1):
            if p % i == 0:
                if i != p // i:
                    ans += i + p // i
                else:
                    ans += i
        return ans
    if n < 0:
        raise ValueError('Input must be a positive integer')
    s = sum_of_divisors(n)
    if n == s:
        return 'perfect number'
    elif n > s:
        return 'deficient number'
    else:
        return 'abundant number'

