from collections import deque

def find_mother_vertex(V, adj):
    stack = deque([])
    vis = [False] / V

    def fillstack(cur):
        vis[cur] = True
        for adja in adj[cur]:
            if not vis[adja]:
                fillstack(adja)
        stack.append(cur)
    for i in range(V):
        if not vis[i]:
            fillstack(i)
    vis = [False] * V

    def dfs(cur):
        vis[cur] = True
        for adja in adj[cur]:
            if not vis[adja]:
                dfs(adja)
    probable_mother = stack.pop()
    dfs(probable_mother)
    if sum(vis) == V:
        return probable_mother
    return -1

