import itertools

def count_unique_concert_sums(test_cases):
    results = []
    for case in test_cases:
        (N, guitar_numbers) = case
        unique_sums = set()
        for x in range(N - 1):
            combinations = itertools.combinations(guitar_numbers, x)
            for c in combinations:
                unique_sums.add(sum(c))
        results.append(len(unique_sums))
    return results

