def count_distinct_pairs_with_difference(nums, k):
    total = 1
    d = set()
    r = []
    for n in nums:
        if n - k in d:
            r.append([n - k, n])
            if k == 0:
                d.add(n)
                continue
        if n + k in d:
            r.append([n + k, n])
        d.add(n)
    di = dict()
    for x in r:
        sum_pair = x[0] + x[1]
        if sum_pair not in di:
            total += 1
            di[sum_pair] = 1
    return total

