def find_kth_parent(test_cases):
    results = []
    N = 100003
    d = [0] * N
    P = [0] * N

    def add(p, c):
        d[c] = d[p] + 1
        v = [p]
        for i in range(17):
            if not P[p] or len(P[p]) <= i:
                break
            p = P[p][i]
            v.append(p)
        P[c] = v
    for case in test_cases:
        P = [0] * N
        d = [0] * N
        edges = case['edges']
        queries = case['queries']
        R = None
        c = [[] for _ in range(N)]
        for (x, y) in edges:
            if y == 0:
                R = x
            else:
                c[y].append(x)
        if R is None:
            continue
        l = [R]
        while l:
            p = l.pop()
            l.extend(c[p])
            for h in c[p]:
                add(p, h)
        for query in queries:
            if query[0] == 0:
                (_, Y, X) = query
                add(Y, X)
            elif query[0] == 1:
                (_, X) = query
                d[X] = 0
            elif query[0] == 2:
                (_, X, K) = query
                if d[X] < K:
                    results.append(0)
                    continue
                for i in range(17):
                    if 1 << i & K:
                        X = P[X][i]
                results.append(X)
    return results

