def sort_string_special(S: str) -> str:

    def value_of_char(char: str) -> tuple:
        value = 1
        if char.isdigit():
            digit = int(char)
            value = (3, 2 if digit % 2 == 0 else 1)
        elif char.islower():
            value = (1,)
        elif char.isupper():
            value = (2,)
        return (value, char)
    return ''.join(sorted(S, key=value_of_char))

