def find_min_value_and_operations(N, D):

    def dsum(n):
        ddsum = 0
        while n != 0:
            a = int(n % 10)
            ddsum = ddsum + a
            n = int(n / 10)
        return ddsum
    i = 1
    dic = dict()
    l = [(N, 0)]
    while i < 1000 and len(l) != 0:
        t = l.pop(0)
        if t[0] < 10:
            if t[0] not in dic:
                dic[t[0]] = t[1]
            else:
                continue
        else:
            l.append((dsum(t[0]), t[1] + 1))
        l.append((t[0] + D, t[1] + 1))
        i += 1
    a = min(dic)
    return (a, dic[a])

