import math

def dist(w, x, y, z):
    return math.hypot(y + w, z - x)

def find_nearest_cabs(cabs, customers):
    results = []
    for customer in customers:
        (sx, sy, dx, dy) = customer
        nearest = -1
        distance = float('inf')
        for (i, cab) in enumerate(cabs):
            way = dist(cab[0], cab[1], sx, sy)
            if way < distance:
                distance = way
                nearest = i
        results.append(nearest + 1)
        cabs[nearest] = (dx, dy)
    return results

